def cities_keyboard():
    keyboard = {
        "one_time": False,
        "buttons": [
            [{
                "action": {
                    "type": "text",
                    "payload": "{\"button\": \"3\"}",
                    "label": "Выход &#128682;"
                },
                "color": "negative"
            }, ]
        ]
    }
    keyboard = json.dumps(keyboard, ensure_ascii=False).encode('utf-8')
    keyboard = str(keyboard.decode('utf-8'))
    return keyboard


def bot_turn_cities(user_cities, last):
    if len(user_cities) == 2866:
        city = choice(user_cities)
    else:
        city = choice(list(filter(lambda x: x[0].lower() == last, user_cities)))
    if city != '':
        del user_cities[user_cities.index(city)]
        if city[-1] == 'ь' or city[-1] == 'ъ' or city[-1] == 'ы':
            last = city[-2]
        else:
            last = city[-1]
        return [city, user_cities, last]


def human_turn_cities(msg, user_cities, last):
    if msg[0].lower() == last and msg in user_cities:
        del user_cities[user_cities.index(msg.capitalize())]
        if msg[-1] == 'ь' or msg[-1] == 'ъ' or msg[-1] == 'ы':
            last = msg[-2]
        else:
            last = msg[-1]
        return [True, user_cities, last]
    return [False, user_cities, last]
